netlist:
* Multiple Feedback Bandpass Filter
.param R1=10k
.param R2=10k
.param R3=10k
.param C1=10n
.param C2=10n
.subckt ideal_opamp inp inn out
E1 out 0 inp inn 100k
.ends ideal_opamp
V1 in 0 dc 0 ac 1
R1 in n1 {R1}
R3 out n1 {R3}
C1 n1 n2 {C1}
R2 n2 0 {R2}
C2 out n2 {C2}
X1 0 n2 out ideal_opamp
